procedure ReEnableControls (DataSet: TDataSet);
begin
while DataSet.ControlsDisabled do
  DataSet.EnableControls;
end;

void __fastcall TForm1::ReEnableControls(TDataSet *DataSet)
{
while (DataSet->ControlsDisabled())
    DataSet->EnableControls();
}

